/*Table structure for table `productocp` */

DROP TABLE IF EXISTS `productocp`;

CREATE TABLE `productocp` (
  `codigo_producto` int(11) NOT NULL auto_increment,
  `nombre` varchar(200) character set utf8 collate utf8_spanish2_ci NOT NULL,
  `descripcion` text character set utf8 collate utf8_spanish2_ci NOT NULL,
  `stockmaximo` int(11) unsigned zerofill default NULL,
  `stockaux` int(11) unsigned zerofill default NULL,
  `stock` int(11) unsigned zerofill default NULL,
  `aux_ruc_proveedor` varchar(13) character set utf8 collate utf8_spanish2_ci default NULL,
  `codigo_nota_fk` varchar(50) character set utf8 collate utf8_spanish2_ci default NULL,
  `valor_unitario` bigint(20) unsigned zerofill default NULL,
  `prioridad` int(11) default '0',
  `medida` varchar(200) character set utf8 collate utf8_spanish2_ci default NULL,
  `valor_total` bigint(20) unsigned zerofill default NULL,
  `precio_promedio` bigint(20) unsigned zerofill default '00000000000000000000',
  `es_pp` int(11) default '0',
  `tipo` varchar(20) default NULL,
  `fecha` date default NULL,
  `ni` int(11) default NULL,
  `cuenta` varchar(50) default NULL,
  PRIMARY KEY  (`codigo_producto`),
  KEY `FK_producto` (`codigo_nota_fk`),
  KEY `FK_producto_fk2` (`aux_ruc_proveedor`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

